--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=44 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (1,1,'2015-02-23 11:55:00','2015-02-23 11:55:00','Bienvenido a WordPress. Esta es tu primera entrada. Edítala o bórrala, ¡y comienza a publicar!.','¡Hola mundo!','','publish','open','open','','hola-mundo','','','2015-02-23 11:55:00','2015-02-23 11:55:00','',0,'http://localhost/Proyecto/wordpress/?p=1',0,'post','',1),(2,1,'2015-02-23 11:55:00','2015-02-23 11:55:00','Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto:\n\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n...o algo así:\n\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo \"cosas\" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href=\"http://localhost/Proyecto/wordpress/wp-admin/\">tu escritorio</a> para borrar esta página y crear páginas nuevas con tu propio contenido. ¡Pásalo bien!','Página de ejemplo','','publish','open','open','','pagina-ejemplo','','','2015-02-23 11:55:00','2015-02-23 11:55:00','',0,'http://localhost/Proyecto/wordpress/?page_id=2',0,'page','',0),(3,1,'2015-02-23 11:55:10','0000-00-00 00:00:00','','Borrador automático','','auto-draft','open','open','','','','','2015-02-23 11:55:10','0000-00-00 00:00:00','',0,'http://localhost/Proyecto/wordpress/?p=3',0,'post','',0),(5,1,'2015-02-23 11:56:45','2015-02-23 11:56:45','','Shop','','publish','closed','open','','shop','','','2015-02-23 11:56:45','2015-02-23 11:56:45','',0,'http://localhost/Proyecto/wordpress/?page_id=5',0,'page','',0),(6,1,'2015-02-23 11:56:45','2015-02-23 11:56:45','[woocommerce_cart]','Cart','','publish','closed','open','','cart','','','2015-02-23 11:56:45','2015-02-23 11:56:45','',0,'http://localhost/Proyecto/wordpress/?page_id=6',0,'page','',0),(7,1,'2015-02-23 11:56:45','2015-02-23 11:56:45','[woocommerce_checkout]','Checkout','','publish','closed','open','','checkout','','','2015-02-23 11:56:45','2015-02-23 11:56:45','',0,'http://localhost/Proyecto/wordpress/?page_id=7',0,'page','',0),(8,1,'2015-02-23 11:56:45','2015-02-23 11:56:45','[woocommerce_my_account]','My Account','','publish','closed','open','','my-account','','','2015-02-23 11:56:45','2015-02-23 11:56:45','',0,'http://localhost/Proyecto/wordpress/?page_id=8',0,'page','',0),(10,1,'2015-02-24 08:42:56','2015-02-24 08:42:56','<a href=\"http://localhost/Proyecto/wordpress/wp-content/uploads/2015/02/bota-menu-velocidad.jpg\"><img class=\"alignnone size-full wp-image-11\" src=\"http://localhost/Proyecto/wordpress/wp-content/uploads/2015/02/bota-menu-velocidad.jpg\" alt=\"bota-menu-velocidad\" width=\"150\" height=\"149\" /></a>','Zapatillas','Botas para patinaje de velocidad','publish','open','closed','','zapatillas','','','2015-02-24 08:44:22','2015-02-24 08:44:22','',0,'http://localhost/Proyecto/wordpress/?post_type=product&#038;p=10',0,'product','',0),(11,1,'2015-02-24 08:42:52','2015-02-24 08:42:52','','bota-menu-velocidad','','inherit','open','open','','bota-menu-velocidad','','','2015-02-24 08:42:52','2015-02-24 08:42:52','',10,'http://localhost/Proyecto/wordpress/wp-content/uploads/2015/02/bota-menu-velocidad.jpg',0,'attachment','image/jpeg',0),(12,1,'2015-02-24 08:53:38','2015-02-24 08:53:38','','pmasports-logo-1424441141','','inherit','open','open','','pmasports-logo-1424441141','','','2015-02-24 08:53:38','2015-02-24 08:53:38','',0,'http://localhost/Proyecto/wordpress/wp-content/uploads/2015/02/pmasports-logo-1424441141.jpg',0,'attachment','image/jpeg',0),(13,1,'2015-02-24 08:53: